import React, { Component } from 'react'

class UserAccount extends Component {
    state = {
        email: ''
        // // user: {
        //     firstName: String,
        //     lastName: String,
        //     email: {},
        //     password: String,
        //     gender: Boolean,
        //     school: String,
        //     affiliation: Boolean
        // // }
    };

    onChange(e) {
        this.setState({
            email: "e.target.value"
        })
    }

  render() {
      const { user } = this.state
      console.log('the state', user)

    return (
        <div className="login-component">
            <form className="login-container">
                <input type="text" id="email" value="Email" onChange={this.onChange} />
                <input type="text" id="password" value="Password" onChange={this.onChange} />
                <input type="submit" value="Login" />
            </form>
        </div>
    )
  }
}


// main component file, and then reducers are for the api (this will be in the outer file)
