import React, { Component } from 'react'

class UserAccount extends Component {
    constructor(props) {
        super(props)
        this.state = {
            newUser: false,
            firstName: 'First Name',
            lastName: 'Last Name',
            email: 'Email',
            password: 'Password',
            gender: true, // female
            school: 'School',
            affiliation: true // student (vs faculty)
        }

        this.onChange = this.onChange.bind(this)
        this.onSubmit = this.onSubmit.bind(this)
        this.onClick = this.onClick.bind(this)
    }

    onChange(e) {
        // console.log(e.target, e.target.value);
        this.setState({
            [e.target.id]: e.target.value
        })
        console.log(this.state)
    }

    onSubmit(e) {
        e.preventDefault();
        // console.log('submit', e.target);
        console.log('email:', this.state.email)
        console.log('password:', this.state.password)
        this.setState({
            email: '',
            password: ''
        })
    }

    onClick() {
        this.setState({ newUser: !this.state.newUser })
    }

    onValue(e) {
        this.setState({ [e.target.id]: e.target.value })
    }

  render() {
      const { newUser, email, password } = this.state
      // console.log('the state', user)
      const userInfo = ['firstName', 'lastName', 'email', 'password', 'gender', 'school', 'affiliation']

    return (
        <div className="login-component">
            { !newUser ?
            <form className="login-container">
                <input type="email" id="email" placeholder='' value={this.state.email} onChange={this.onChange} />
                <input type="password" id="password" value={this.state.password} onChange={this.onChange} />
                <input type="submit" value="Login" onClick={this.onSubmit} />
                <input type="button" value="Sign up" onClick={this.onClick} />
            </form>
            : <div className='signup-component'>
                    <div className='userinfo-fields'> {
                        userInfo.map((el, i) => {
                            return <div className={el}>
                                <label>{el}</label>
                                <input type='text' id={el} key={i} placeholder=' ' value={this.onValue}
                                onChange={this.onChange} />
                            </div>
                        })
                    }</div>
                    <input type="button" value="Sign up" onClick={this.onClick} />
            </div>}
            </div>
    )
  }
}

